// Access the 'outlineJson' field from the input item
const outlineJson = $json.outlineJson || "";

// Split the outlineJson content into lines
const lines = outlineJson.split(/\\n/);

// Initialize variables for grouping
let currentSection = null;
const sections = [];

// Iterate through the lines and group by sections
lines.forEach((line) => {
  const trimmedLine = line.trim();

  if (trimmedLine.startsWith("##")) {
    // Start a new section
    if (currentSection) {
      sections.push(currentSection);
    }
    currentSection = trimmedLine; // Initialize the new section with the title
  } else if (currentSection && trimmedLine) {
    // Append the point to the current section
    currentSection += "\n" + trimmedLine;
  }
});

// Add the last section if it exists
if (currentSection) {
  sections.push(currentSection);
}

// Create individual outputs for each merged section
return sections.map((section) => ({
  json: {
    mergedSection: section,
  },
}));

