const body = $json;
const required = body.asset_url || body.presigned_get || body.s3_uri;
if (!required) {
  throw new Error('asset_url (or presigned_get / s3_uri) is required');
}
const now = new Date().toISOString();
const namespace = body.namespace || 'pmoves.art';
const persona = body.persona || body.author || 'unknown';
const workflow = body.workflow || 'wan-animate-2.2';
const title = body.title || (body.key ? body.key.split('/').pop() : 'wan-clip');
const slug = (body.slug || title).toLowerCase().replace(/[^a-z0-9]+/g, '-').replace(/^-+|-+$/g, '') || 'wan-clip';
const tags = Array.isArray(body.tags) ? [...body.tags] : [];
if (!tags.includes(`workflow:${workflow}`)) tags.push(`workflow:${workflow}`);
if (persona && !tags.includes(`persona:${persona}`)) tags.push(`persona:${persona}`);
const autoApprove = Boolean(body.auto_approve);
const studioRow = {
  title,
  namespace,
  content_url: body.asset_url || body.presigned_get || body.s3_uri,
  status: autoApprove ? 'approved' : 'submitted',
  meta: {
    author: body.author || persona,
    tags,
    persona,
    workflow,
    presigned_get: body.presigned_get,
    bucket: body.bucket,
    key: body.key,
    prompt: body.prompt,
    negative_prompt: body.negative_prompt,
    reference_image: body.reference_image,
    reference_motion: body.reference_motion,
    duration_sec: body.duration_sec,
    fps: body.fps,
    notes: body.notes,
    source: 'comfyui',
    webhook: true
  }
};
const points = [
  {
    id: `asset:${slug}`,
    modality: 'video',
    ref_id: studioRow.content_url,
    proj: 1.0,
    summary: title
  }
];
if (body.reference_image) {
  points.push({
    id: `ref-image:${slug}`,
    modality: 'image',
    ref_id: body.reference_image,
    proj: 0.6,
    summary: 'reference image'
  });
}
if (body.reference_motion) {
  points.push({
    id: `ref-motion:${slug}`,
    modality: 'video',
    ref_id: body.reference_motion,
    proj: 0.7,
    summary: 'motion reference'
  });
}
const clamp01 = (v, fallback) => {
  if (typeof v !== 'number' || Number.isNaN(v)) return fallback;
  return Math.max(0, Math.min(1, v));
};
const spectrum = [
  clamp01(body.motion_score, 0.85),
  clamp01(body.fidelity_score, 0.75)
];
const constellation = {
  id: `wan.${workflow}.${slug}`,
  summary: body.prompt || title,
  spectrum,
  points,
  meta: {
    namespace,
    persona,
    workflow,
    tags,
    duration_sec: body.duration_sec,
    fps: body.fps
  }
};
const cgp = {
  spec: 'chit.cgp.v0.1',
  summary: `WAN Animate render → ${title}`,
  created_at: now,
  super_nodes: [
    {
      id: `creative:${namespace}`,
      label: namespace,
      summary: body.notes || '',
      constellations: [constellation]
    }
  ],
  meta: {
    source: 'comfy.wan.animate.v2',
    persona,
    tags,
    namespace,
    workflow,
    studio_board_title: title
  }
};
return [{ json: { studio_row: studioRow, cgp_envelope: { type: 'geometry.cgp.v1', data: cgp } } }];

const envelope = $node["Prepare Records"].json.cgp_envelope;
const rows = Array.isArray($json) ? $json : [$json];
let record = null;
for (const r of rows) {
  if (r && typeof r === 'object' && !Array.isArray(r) && 'id' in r) {
    record = r;
    break;
  }
  if (Array.isArray(r)) {
    const inner = r.find(x => x && typeof x === 'object' && 'id' in x);
    if (inner) {
      record = inner;
      break;
    }
  }
}
if (record && record.id) {
  envelope.data.meta = envelope.data.meta || {};
  envelope.data.meta.studio_board_id = record.id;
}
return [{ json: envelope }];
